    <script>
        
        $(""body"").on(""click"", "".removelikeButton"", function () {

            let removeLikeUrl = $(this).data(""url"");
            $.get(removeLikeUrl);

            let commentId = removeLikeUrl.split(""/"").slice(-1).pop()
            
            let currentLikes = Number($(this).parent().next()[0].innerText);
            
